mapper.select:
SELECT 
FROM users
WHERE username = #{usernameOrEmail} OR email = #{usernameOrEmail}

mapper.insert:
INSERT INTO users (
    username, email, password, real_name, phone, status,
    total_answered, correct_answered, accuracy_rate, study_time, interview_count,
    created_at, updated_at
) VALUES (
    #{username}, #{email}, #{password}, #{realName}, #{phone}, #{status},
    #{totalAnswered}, #{correctAnswered}, #{accuracyRate}, #{studyTime}, #{interviewCount},
    #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE users
SET password = #{password}, updated_at = NOW()
WHERE id = #{id}
